mapper.select:
SELECT client_id, base_date, sku_code, sku_name, category, available_qty, days_remaining, status, recommendation
FROM ANALYSIS_RESULT
WHERE base_date = #{baseDate}
  AND client_id = #{clientId}
  AND status IN ('WARNING', 'DANGER')
ORDER BY days_remaining

SELECT COUNT(*)
FROM ANALYSIS_RESULT ar
WHERE ar.base_date = #{baseDate}

    AND ar.client_id = #{clientId}


    AND ar.category = #{category}


    AND ar.status = #{status}

SELECT ar.client_id, c.client_name, ar.base_date, ar.category, ar.sku_code, ar.sku_name, ar.available_qty, ar.avg_daily_outbound_recent7days, ar.avg_daily_outbound_recent28days, COALESCE(DATE_FORMAT(ar.estimated_sold_out_date_recent7days, '%Y-%m-%d'), '-') AS estimated_sold_out_date_recent7days, COALESCE(DATE_FORMAT(ar.estimated_sold_out_date_recent28days, '%Y-%m-%d'), '-') AS estimated_sold_out_date_recent28days, ar.days_remaining, ar.status, ar.recommendation, onh.status AS oms_status FROM ANALYSIS_RESULT ar LEFT JOIN ( SELECT client_id, base_date, status FROM OMS_NOTIFICATION_HISTORY WHERE history_id IN ( SELECT MAX(history_id) FROM OMS_NOTIFICATION_HISTORY GROUP BY client_id, base_date ) ) onh ON ar.client_id = onh.client_id AND ar.base_date = onh.base_date JOIN CLIENT c ON ar.client_id = c.client_id WHERE ar.base_date = #{baseDate}
AND ar.client_id = #{clientId}
AND ar.category = #{category}
AND ar.status = #{status}
ORDER BY ar.category, ar.sku_code LIMIT #{pageSize} OFFSET #{offset}
